text2workspace.py -P HiggsAnalysis.CombinedLimit.PhysicsModel:multiSignalModel  --PO verbose --PO 'map=.*/ggH:r[1,0,10]' --PO 'map=.*/qqH:r' fit.txt workspace.root

combine -M MultiDimFit --algo grid --points 50 --rMin 0.0 --rMax 5.0 -t -1 --setParameters r=1 --redefineSignalPOIs=r fit.root -m 125 -n expected.nominal --cminDefaultMinimizerStrategy 0

combine -M MultiDimFit --algo none --rMin 0.0 --rMax 5.0 -t -1 --setParameters r=1 --redefineSignalPOIs=r fit.root -m 125 -n expected.bestfit --cminDefaultMinimizerStrategy 0 --saveWorkspace

combine -M MultiDimFit --algo grid --points 50 --rMin 0.0 --rMax 5.0 -t -1 --setParameters r=1 --redefineSignalPOIs=r -m 125 -n expected.stat higgsCombineexpected.bestfit.MultiDimFit.mH125.root --snapshotName MultiDimFit --freezeParameters allConstrainedNuisances --cminDefaultMinimizerStrategy 0

plot1DScan.py higgsCombineexpected.nominal.MultiDimFit.mH125.root --main-label "stat. + syst." --POI r --others 'higgsCombineexpected.stat.MultiDimFit.mH125.root:stat.:2' --breakdown syst,stat

combine -M Significance fit.txt -t -1 -m 125 --expectSignal=1 > significance.txt